%% 1#clc;clear;t = linspace(0,2,10001);t = t*pi;a = 2; b = 0.2; c=45;x = cos(t).*(a+b*sin(c*t));y = sin(t).*(a+b*sin(c*t));figure(1)plot(x,y);axis equal;dx = -a*sin(t) + b*(-sin(t).*sin(c*t) + c*cos(t).*cos(c*t));dy = a*cos(t) + b*(cos(t).*sin(c*t) + c*sin(t).*cos(c*t));%kkk1=-abs(dy);[~,peaklocZero] = findpeaks(kkk1);fs = @(t) a*cos(t) + b*(cos(t).*sin(c*t) + c*sin(t).*cos(c*t));options = optimoptions('fsolve','MaxFunEvals',30000);tZero = fsolve(fs,t(peaklocZero),options);dyZero = a*cos(tZero) + b*(cos(tZero).*sin(c*tZero) + c*sin(tZero).*cos(c*tZero));tZero(find(abs(dyZero)>1e-2)) = [];dyZero(find(abs(dyZero)>1e-2)) = [];figureplot(tZero,dyZero,'rd')figure(1)hold onxZero = cos(tZero).*(a+b*sin(c*tZero));yZero = sin(tZero).*(a+b*sin(c*tZero));plot(xZero,yZero,'kd')%kkk2=-abs(dx);[~,peaklocInf] = findpeaks(kkk2);fs = @(t) -a*sin(t) + b*(-sin(t).*sin(c*t) + c*cos(t).*cos(c*t));options = optimoptions('fsolve','MaxFunEvals',30000);tInf = fsolve(fs,t(peaklocInf),options);dyInf = -a*sin(tInf) + b*(-sin(tInf).*sin(c*tInf) + c*cos(tInf).*cos(c*tInf));tInf(find(abs(dyInf)>1e-2)) = [];dyInf(find(abs(dyInf)>1e-2)) = [];figure(1)hold onxInf = cos(tInf).*(a+b*sin(c*tInf));yInf = sin(tInf).*(a+b*sin(c*tInf));plot(xInf,yInf,'rd')for i =1:length(xZero)    figure(1)    plot([0,xZero(i)], [0,yZero(i)],'k')    hold onendfor i =1:length(xInf)    figure(1)    plot([0,xInf(i)], [0,yInf(i)],'r')    hold on    end